import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';

// Componente funcional ProductList que recibe la lista de productos como prop
const ProductList = ({ productList }) => {
  return (
    <div>
      <h2>Lista de Productos</h2>
      <ul>
        {productList.map(product => (
          <li key={product.id}>{product.nombre}</li>
        ))}
      </ul>
    </div>
  );
};

// Función asincrónica para obtener la lista de productos desde la API
const fetchProduct = async () => {
  try {
    const response = await axios.get('http://localhost:8000/api/productos/');
    return response.data.productos;
  } catch (error) {
    throw error;
  }
};

// Función para renderizar la aplicación
const renderApp = async () => {
  try {
    // Obtener la lista de productos
    const productList = await fetchProduct();
    // Renderizar el componente ProductList con la lista de productos como prop
    ReactDOM.createRoot(document.getElementById('root')).render(<ProductList productList={productList} />);
  } catch (error) {
    // En caso de error, renderizar un mensaje de error
    ReactDOM.createRoot(document.getElementById('root')).render(<p>Error al cargar el producto: {error.message}</p>);
  }
};

// Llamar a la función renderApp para iniciar la aplicación
renderApp();
