import React, { useEffect } from 'react';
import axios from 'axios';

const ProductList = () => {
  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/productos/');
        console.log(response.data.productos); // Solo mostramos los productos en la consola por ahora
      } catch (error) {
        console.error('Error fetching product list:', error);
      }
    };

    fetchProduct(); // Llamamos a la función para obtener los productos al montar el componente
  }, []); // El segundo argumento del useEffect es un array vacío para asegurar que solo se ejecute una vez al montar el componente

  return (
    <div>
      {/* Aquí podríamos agregar un mensaje de carga o cualquier otro contenido */}
    </div>
  );
};

export default ProductList;
