import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';

const fetchProduct = async () => {
  try {
    const response = await axios.get('http://localhost:8000/api/productos/');
    return response.data.productos;
  } catch (error) {
    throw error;
  }
};

const renderApp = async () => {
  try {
    const productList = await fetchProduct();
    ReactDOM.createRoot(document.getElementById('root')).render(<ProductList product={productList} />);
  } catch (error) {
    ReactDOM.createRoot(document.getElementById('root')).render(<p>Error al cargar el producto: {error.message}</p>);
  }
};

renderApp();

export default ProductList;
