import React, { useState, useEffect } from 'react';
import axios from 'axios';
import coquetaImage from '../assets/coqueta.jpeg'; // Importa la imagen

const ProductList = () => {
  const [productList, setProductList] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/productos/');
        setProductList(response.data.productos);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  return (
    <div>
      <h2>Lista de Productos</h2>
      {productList.map(product => (
        <div key={product.id} className="product-banner" style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection: 'column' }}>
          <img src={coquetaImage} alt={product.nombre} style={{ maxWidth: '100%', maxHeight: '100%', width: 'auto', height: 'auto' }} /> {/* Utiliza la imagen importada y aplica estilos en l√≠nea */}
          <div className="product-details">
            <h3>{product.nombre}</h3>
            <p>Precio: ${product.precio}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ProductList;
