import React, { useState, useEffect } from 'react';
import { createRoot } from 'react-dom';
import axios from 'axios';
import ProductListComponent from './components/productList';

const ProductList = () => {
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchProduct = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/productos/');
      setProduct(response.data.productos);
    } catch (error) {
      setError(error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProduct();
  }, []);

  if (loading) {
    return <p>Cargando producto...</p>;
  }

  if (error) {
    return <p>Error al cargar el producto: {error.message}</p>;
  }

  return (
    <div>
      <h2>Lista de Productos</h2>
      <ul>
        {product.map(product => (
          <li key={product.id}>{product.nombre}</li>
        ))}
      </ul>
    </div>
  );
};

// Utiliza createRoot para renderizar tu aplicación
const root = document.getElementById('root');
createRoot(root).render(<ProductList />);
