import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <div>
      <div className="hero-banner">
        <div className="container">
          <h1>Bienvenido a nuestra tienda de mascotas</h1>
          <p>Descubre nuestra increíble selección de productos para tus mascotas.</p>
          <Link to="/productos" className="btn btn-primary">Ver productos</Link>
        </div>
      </div>
      <div className="container">
        <div className="row">
          <div className="col">
            <h2>Producto destacado</h2>
            <img src="/coqueta.jpeg" alt="Producto destacado" />
            <p>¡Este increíble producto es perfecto para tu mascota!</p>
            <Link to="/productos" className="btn btn-primary">Ver producto</Link>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
