import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';

// Función asincrónica para obtener la lista de productos desde la API
const fetchProduct = async () => {
  try {
    const response = await axios.get('http://localhost:8000/api/productos/');
    return response.data.productos;
  } catch (error) {
    throw error;
  }
};

// Componente funcional ProductList que recibe la lista de productos como prop
const ProductList = ({ productList }) => {
  return (
    <div>
      <h2>Lista de Productos</h2>
      <ul>
        {productList.map(product => (
          <li key={product.id}>{product.nombre}</li>
        ))}
      </ul>
    </div>
  );
};

// Llamar a la función fetchProduct para obtener la lista de productos y luego renderizar el componente ProductList
fetchProduct().then(productList => {
  ReactDOM.createRoot(document.getElementById('root')).render(<ProductList productList={productList} />);
});

export default ProductList;
