import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import ProductListComponent from './productList'; // Cambiar el nombre de la importación
// Asegúrate de que la ruta del archivo sea correcta

const ProductList = () => {
  // Estado para almacenar los datos del producto
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Función para obtener los datos del producto desde la API
  const fetchProduct = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/productos/'); // Cambia la URL y el ID según tu API
      setProduct(response.data.productos);
    } catch (error) {
      setError(error);
    }
  };

  // Efecto para cargar los datos del producto al montar el componente
  useEffect(() => {
    fetchProduct();
  }, []);

  // Renderizar indicador de carga si se están obteniendo los datos
  if (loading) {
    return <p>Cargando producto...</p>;
  }

  // Renderizar mensaje de error si ocurrió un error al obtener los datos
  if (error) {
    return <p>Error al cargar el producto: {error.message}</p>;
  }

  // Renderizar el componente ProductListComponent y pasar el producto como prop
  return (
    <div>
      <h2>Lista de Productos</h2>
      <ul>
        {products.map(product => (
          <li key={product.id}>{product.nombre}</li>
        ))}
      </ul>
    </div>
  );
};

// Renderizamos el componente ProductList en el elemento con id 'root'
ReactDOM.render(<ProductList />, document.getElementById('root'));

export default ProductList;
