import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import ProductList from './productList'; // Asegúrate de que la ruta del archivo sea correcta

const Test = () => {
  // Estado para almacenar los datos del producto
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Función para obtener los datos del producto desde la API
  const fetchProduct = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/productos/'); // Cambia la URL y el ID según tu API
      setProduct(response.data.productos);
    } catch (error) {
      setError(error);
    }
  };

  // Efecto para cargar los datos del producto al montar el componente
  useEffect(() => {
    fetchProduct();
  }, []);

  // Renderizar indicador de carga si se están obteniendo los datos
  if (loading) {
    return <p>Cargando producto...</p>;
  }

  // Renderizar mensaje de error si ocurrió un error al obtener los datos
  if (error) {
    return <p>Error al cargar el producto: {error.message}</p>;
  }

  // Renderizar el componente ProductList y pasar el producto como prop
  return (
    <div>
      <ProductList product={product} />
    </div>
  );
};

// Renderizamos el componente Test en el elemento con id 'root'
ReactDOM.render(<Test />, document.getElementById('root'));

export default Test;
